export default function InvitationCard() {
  return (
    <div className="bg-giGreen p-6 rounded-xl max-w-xl mx-auto">
      {/* Judul / Header Card */}
      <h2 className="text-white text-2xl font-bold mb-6 text-center">
        Undangan Bergabung
      </h2>

      {/* Card dalam */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Card User Umum */}
        <div className="bg-white shadow-lg rounded-lg p-4 flex flex-col items-center">
          <h3 className="text-lg font-semibold mb-2">User Umum</h3>
          <p className="text-gray-600 text-center text-sm">
            Akses informasi gizi lengkap dan fitur pencarian bahan pangan.
          </p>
        </div>

        {/* Card Pelaku UMKM */}
        <div className="bg-white shadow-lg rounded-lg p-4 flex flex-col items-center">
          <h3 className="text-lg font-semibold mb-2">Pelaku UMKM</h3>
          <p className="text-gray-600 text-center text-sm">
            Kelola dan tampilkan informasi nilai gizi produk Anda dengan mudah.
          </p>
        </div>
      </div>
    </div>
  );
}
