import { useEffect, useState } from "react";
import { Menu, X } from "lucide-react";
import { Link } from "react-router-dom";

export default function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  // Event listener untuk scroll
  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 0);
    };
    window.addEventListener("scroll", handleScroll);

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <nav
      className={`fixed w-full top-0 z-50 px-6 py-4 transition-shadow duration-300 ${
        scrolled ? "shadow-md bg-giGreen" : "bg-giGreen"
      }`}
    >
      <div className="max-w-7xl mx-auto flex items-center justify-between">
        {/* Logo kiri */}
        <div className="text-2xl font-bold text-white">GiMultiLab</div>

        {/* Menu tengah */}
        <ul className="hidden md:flex gap-8 text-white font-medium">
          <li><Link to="/" className="hover:text-opacity-80">Beranda</Link></li>
          <li><Link to="/tentang" className="hover:text-opacity-80">Tentang</Link></li>
          <li><Link to="/fitur" className="hover:text-opacity-80">Fitur</Link></li>
        </ul>

        {/* Login kanan */}
        <div className="hidden md:block">
          <Link
            to="/login"
            className="bg-white hover:bg-opacity-90 text-giGreen px-4 py-2 rounded-md"
          >
            Login
          </Link>
        </div>

        {/* Hamburger mobile */}
        <div className="md:hidden">
          <button onClick={() => setIsOpen(!isOpen)}>
            {isOpen ? <X className="w-6 h-6 text-white" /> : <Menu className="w-6 h-6 text-white" />}
          </button>
        </div>
      </div>

      {/* Menu mobile */}
      {isOpen && (
        <div className="md:hidden mt-4 space-y-2 px-4 pb-4 border-t pt-4 bg-giGreen">
          <Link to="/" className="block text-white hover:text-opacity-80">Beranda</Link>
          <Link to="/tentang" className="block text-white hover:text-opacity-80">Tentang</Link>
          <Link to="/fitur" className="block text-white hover:text-opacity-80">Fitur</Link>
          <Link
            to="/login"
            className="block bg-white text-giGreen hover:bg-opacity-90 px-3 py-2 rounded-md w-fit mt-2"
          >
            Login
          </Link>
        </div>
      )}
    </nav>
  );
}
