import { useState } from 'react';
import { Menu, X } from 'lucide-react';
import { Link } from 'react-router-dom';

export default function Navbar() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="bg-white shadow-md px-6 py-4 sticky top-0 z-50">
      <div className="max-w-7xl mx-auto flex items-center justify-between">
        {/* Kiri - Logo */}
        <div className="text-2xl font-bold text-giGreen">GiMultiLab</div>

        {/* Tengah - Menu desktop */}
        <ul className="hidden md:flex gap-8 text-giGreen font-medium">
          <li><Link to="/" className="hover:text-opacity-80">Beranda</Link></li>
          <li><Link to="/tentang" className="hover:text-opacity-80">Tentang</Link></li>
          <li><Link to="/fitur" className="hover:text-opacity-80">Fitur</Link></li>
        </ul>

        {/* Kanan - Login */}
        <div className="hidden md:block">
          <Link
            to="/login"
            className="bg-giGreen hover:bg-opacity-90 text-white px-4 py-2 rounded-md"
          >
            Login
          </Link>
        </div>

        {/* Hamburger Menu - Mobile */}
        <div className="md:hidden">
          <button onClick={() => setIsOpen(!isOpen)}>
            {isOpen ? <X className="w-6 h-6 text-giGreen" /> : <Menu className="w-6 h-6 text-giGreen" />}
          </button>
        </div>
      </div>

      {/* Mobile Dropdown Menu */}
      {isOpen && (
        <div className="md:hidden mt-4 space-y-2 px-4 pb-4 border-t pt-4 bg-white">
          <Link to="/" className="block text-giGreen hover:text-opacity-80">Beranda</Link>
          <Link to="/tentang" className="block text-giGreen hover:text-opacity-80">Tentang</Link>
          <Link to="/fitur" className="block text-giGreen hover:text-opacity-80">Fitur</Link>
          <Link
            to="/login"
            className="block bg-giGreen hover:bg-opacity-90 text-white px-3 py-2 rounded-md w-fit mt-2"
          >
            Login
          </Link>
        </div>
      )}
    </nav>
  );
}
